class Solution:

    def kItemsWithMaximumSum(self, numOnes: int, numZeros: int, numNegOnes: int, k: int) -> int:
        max_sum = float('-inf')
        for x in range(numOnes + 1):
            for y in range(numZeros + 1):
                for z in range(numNegOnes + 1):
                    if x + y + z == k:
                        curr_sum = x - z
                        max_sum = max(max_sum, curr_sum)
        return max_sum

