class Solution:

    def findMatrix(self, nums: List[int]) -> List[List[int]]:
        self.result = []

        def helper(index, matrix):
            if index == len(nums):
                for row in matrix:
                    if len(set(row)) != len(row):
                        return
                if not self.result or len(matrix) < len(self.result):
                    self.result = copy.deepcopy(matrix)
                return
            for i in range(len(matrix)):
                if nums[index] not in matrix[i]:
                    matrix[i].append(nums[index])
                    helper(index + 1, matrix)
                    matrix[i].pop()
            matrix.append([nums[index]])
            helper(index + 1, matrix)
            matrix.pop()
        helper(0, [])
        return self.result

