class Solution:

    def findMatrix(self, nums: List[int]) -> List[List[int]]:
        frequency = {}
        for num in nums:
            if num in frequency:
                frequency[num] += 1
            else:
                frequency[num] = 1
        sorted_frequency = sorted(frequency.items(), key=lambda x: x[1], reverse=True)
        result = []
        for (num, freq) in sorted_frequency:
            for i in range(freq):
                if i < len(result):
                    result[i].append(num)
                else:
                    result.append([num])
        return result
