class Solution:

    def pair_difference(self, nums: List[int], pair: tuple) -> int:
        return abs(nums[pair[0]] - nums[pair[1]])

    def minimizeMax(self, nums: List[int], p: int) -> int:
        index_pairs = [(i, j) for i in range(len(nums)) for j in range(i + 1, len(nums))]
        min_max_diff = float('inf')
        for pairs in combinations(index_pairs, p):
            max_diff = max((self.pair_difference(nums, pair) for pair in pairs))
            min_max_diff = min(min_max_diff, max_diff)
        return min_max_diff

